sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;
	//console.log('harley console log Client Script');
	//jslog('harley jslog client script');
	c.selectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID['+idx+']: ' + id);
		$rootScope.idx=idx;
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}

	$rootScope.$on('refreshNote', function(event,data) { 
			console.log('Listener caught refreshNote NoteID: ' + $rootScope.noteID);
		//console.log('harley event: '+JSON.stringify(event));
		console.log('harley data: '+JSON.stringify(data));
		c.data.notes[data].title=$rootScope.noteTitle;
		c.data.notes[data].note=$rootScope.noteBody;
		});
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.query();
	while (noteGR.next()) {
		var noteObj = {};
		//use service portal helper method to get some display values
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number, title,sys_id');
		//gs.warn('harley noteObj: '+JSON.stringify(noteObj));
		//get the first 20 characters of the description
		noteObj.note = noteGR.getValue('note').slice(0,20);
		data.notes.push(noteObj);
	}
//console.log('Harley console log Server Script');
})();
